import React, { useState } from "react";

const FORM_ENDPOINT = "https://public.herotofu.com/v1/20afd840-d86e-11ec-9d7b-556d531b7d73"; // TODO - fill on the later step

const ContactForm = () => {
  const [submitted, setSubmitted] = useState(false);
  const handleSubmit = () => {
    setTimeout(() => {
      setSubmitted(true);
    }, 100);
  };

  if (submitted) {
    return (
      <>
        <div className="">Thank you!</div>
        <div className="text-md">We'll be in touch soon.</div>
      </>
    );
  }

  return (
    <main>
        <form
      action={FORM_ENDPOINT}
      onSubmit={handleSubmit}
      method="POST"
      target="_blank"
    >
      <div className="mb-3 pt-0">
        <input
          type="text"
          placeholder="Your name"
          name="name"
          required
        />
      </div>
      <div className="mb-3 pt-0">
        <input
          type="email"
          placeholder="Email"
          name="email"
          required
        />
        <div className="telefone">
        <input type="text">
    
        </input>
        </div>

      </div>
      <div >
        <textarea
          placeholder="Your message"
          name="message"
          required
        />
      </div>
      <div>
        <button
          type="submit"
        >
          Send a message
        </button>
      </div>
    </form>
    </main>
  );
};

export default ContactForm;
