import { render } from "express/lib/response";
import React from "react";
import ReactDOM from "react-dom"

// Criand o Component

class Formulario extends React.Component{


    //Construtor
    constructor(props){
        super(props);

        this.state = {
            nome:'',
            email:'',
            telefone:'',
            mensagem:'',
            valor:null
        }
    }


    //evento Teclado
    eventoTeclado = (e) => {
        let nome = e.target.name;
        let email = e.target.email;
        let telefone = e.target.telefone;
        let mensagem = e.target.mensagem;
        let valor = e.target.value;

        this.setState({[nome]:valor })
        this.setState({[mensagem]:valor })
        this.setState({[telefone]:valor })
        this.setState({[email]:valor })
    }


    //executa a ação enviar Formulario

    enviar = (evento) => {

        //Não realiza a atualização da pagina
        evento.preventDefault();

        //mensagem
        alert('Produto'+this.state.nome+this.state.email+this.state.telefone+this.state.mensagem);
    }


    //render

    render(){
        return (
            <main>
                <form onSubmit={this.enviar}>
                    <input type="text" name="nome" placeholder="nome" onChange={this.eventoTeclado} />
                    <input type="text" name="email" placeholder="email" onChange={this.eventoTeclado} />
                    <input type="text" name="telefone" placeholder="telefone" onChange={this.eventoTeclado} />
                    <input type="text" name="mensagem" placeholder="telefone" onChange={this.eventoTeclado} />
                    <input type="submit" />
                </form>
            </main>
        );

    }  
 }

 export default Formulario;
